package Operators;

public class ArithmeticMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//declare variables
				int a=12,b=5;
				//addition operator
				System.out.println("a+b=" +(a+b));
				//substraction operator
				System.out.println("a-b=" +(a-b));
				//Multiplication operator
				System.out.println("a*b=" +(a*b));
				//Division Operator
				System.out.println("a/b=" +(a/b));
				//Modulo Operator
		        System.out.println("a%b=" +(a%b));

	}

}
